import React, { useEffect, useState} from 'react';
import './styles.css';
import '../../bootstrap-4.5.3-dist/css/bootstrap.min.css';

import Potion from '../../components/Potion';

const Potions = () => {
    interface Potion {
        imgSrc: string;
        title: string;
        text: string;
    }

    const [potions, setPotions] = useState<Potion[]>([]);

    useEffect(() => {
        const potion: Potion = {
            imgSrc: 'http://clipart-library.com/newhp/Halloween_Potion_PNG_Clip_Art_Image.png',
            title: 'Poção do Envelhecimento',
            text: 'Esta poção tem o poder de envelhar uma pessoa cerca de 5 anos por gota.'
        };
        setPotions([ ...potions, potion ])
        alert(potions);
    }, []);

    return (
        <div id="potions-page">
            <h1><br/>Qual iguaria você levará hoje?</h1>
            <h4><br/><br/>
                Selecione o que quiser, sinta-se a vontade.
            </h4>
            <div className="content">
                {
                    potions.map(item => (
                        Potion(item.imgSrc, item.title, item.text)
                    ))
                    //Potion('http://clipart-library.com/newhp/Halloween_Potion_PNG_Clip_Art_Image.png', 'Poção do Envelhecimento', 'Esta poção tem o poder de envelhar uma pessoa cerca de 5 anos por gota.')
                }
            </div>
        </div>
    );
};

export default Potions;
