import React from 'react';
import { JsxElement } from 'typescript';
import '../../bootstrap-4.5.3-dist/css/bootstrap.min.css';
import './styles.css';

const Potion = (
    imgSource: JsxElement,
    title: string,
    text: string
) => {
    const checkTitle = title ? title : 'Empty title';
    const checkText = text ? text : 'Empty text';
    const checkImg = imgSource ? imgSource : '/src/assets/empty.png';
    alert(checkImg);
    return (
        <div className="card">
            <img src={checkImg} className="card-img-top" alt="image"/>
            <div className="card-body">
                <h5 className="card-title">{checkTitle}</h5>
                <p className="card-text">{checkText}</p>
                <button className="btn btn-dark">Comprar</button>
            </div>
        </div>
    );
}

export default Potion;
