import React from 'react';
//import { useEffect, useState} from 'react';
import './styles.css';
import '../../bootstrap-4.5.3-dist/css/bootstrap.min.css';

import Potion from '../../components/Potion';

interface potionsReply {
    props: {
        content: [{
            name: string,
            description: string,
            img: string
        }]
    }
}

const Potions: React.FC<potionsReply> = ({props}) => {
    //const [potions, setPotions] = useState<potionsReply>();

    return (
        <div id="potions-page">
            <h1><br/>Qual iguaria você levará hoje?</h1>
            <h4><br/><br/>
                Selecione o que quiser, sinta-se a vontade.
                <br/><br/>
            </h4>
            <div className="potions">
                {
                    props.content.map(item => 
                        <Potion imgSource={item.img} title={item.name} text={item.description} price='US$15.70'></Potion>
                    )
                }
            </div>
        </div>
    );
};

export default Potions;
