import React from 'react';
import '../../bootstrap-4.5.3-dist/css/bootstrap.min.css';
import './styles.css';

const Potion = (
    imgSource: string,
    title: string,
    text: string
) => {
    const checkTitle = title ? title : "Empty title";
    const checkText = text ? text : "Empty text";
    const checkImg = imgSource ? imgSource : "../../assets/empty.png";

    return (
        <div className="card">
            <img src={imgSource} className="card-img-top" alt="..." />
            <div className="card-body">
                <h5 className="card-title">{title}</h5>
                <p className="card-text">{text}</p>
                <a className="btn btn-primary">Comprar</a>
            </div>
        </div>
    );
}

export default Potion;
