import React, { useContext, useEffect, useState } from 'react';
//import { useEffect, useState} from 'react';
import './styles.css';
import '../../bootstrap-4.5.3-dist/css/bootstrap.min.css';

import PotionsContext from '../../contexts/potions';

import Potion from '../../components/Potion';

interface potionsReply {
    content: [{
        name: string,
        description: string,
        img: string
    }]
}

const Potions = () => {
    const [potions, setPotions] = useState<potionsReply>();
    const { getPotions } = useContext(PotionsContext);

    useEffect(() => {
        async function getAll() {
            const potions: potionsReply = await getPotions();
            console.log(potions);
        }
    }, []);

    return (
        <div id="potions-page">
            <h1><br/>Qual iguaria você levará hoje?</h1>
            <h4><br/><br/>
                Selecione o que quiser, sinta-se a vontade.
                <br/><br/>
            </h4>
            <div className="potions">
                {
                    /*potions.map(item => (
                        Potion(item.imgSrc, item.title, item.text)
                    ))*/
                    <Potion imgSource='http://clipart-library.com/newhp/Halloween_Potion_PNG_Clip_Art_Image.png' title='Poção do Envelhetriz' text='Esta poção tem o poder de envelhar uma pessoa cerca de 5 anos por gota.' price='US$15.70'></Potion>
                }
            </div>
        </div>
    );
};

export default Potions;
